export declare const PROMPT = "\u4F7F\u7528\u6B64\u5DE5\u5177\u6267\u884C\u57FA\u672C\u7684\u6570\u5B66\u8FD0\u7B97\u3002\n\n\u652F\u6301\u7684\u8FD0\u7B97\u7C7B\u578B:\n- add (\u52A0\u6CD5): \u8BA1\u7B97\u4E24\u4E2A\u6570\u5B57\u7684\u548C\n- subtract (\u51CF\u6CD5): \u8BA1\u7B97\u4E24\u4E2A\u6570\u5B57\u7684\u5DEE\n- multiply (\u4E58\u6CD5): \u8BA1\u7B97\u4E24\u4E2A\u6570\u5B57\u7684\u79EF\n- divide (\u9664\u6CD5): \u8BA1\u7B97\u4E24\u4E2A\u6570\u5B57\u7684\u5546\n\n\u53C2\u6570\u8BF4\u660E:\n- operation: \u8FD0\u7B97\u7C7B\u578B\uFF0C\u5FC5\u987B\u662F add\u3001subtract\u3001multiply \u6216 divide \u4E4B\u4E00\n- a: \u7B2C\u4E00\u4E2A\u6570\u5B57\uFF08\u88AB\u8FD0\u7B97\u6570\uFF09\n- b: \u7B2C\u4E8C\u4E2A\u6570\u5B57\uFF08\u8FD0\u7B97\u6570\uFF09\n\n\u8FD4\u56DE\u7ED3\u679C:\n- ok: \u64CD\u4F5C\u662F\u5426\u6210\u529F\n- operation: \u6267\u884C\u7684\u8FD0\u7B97\u7C7B\u578B\n- operands: \u64CD\u4F5C\u6570 { a, b }\n- result: \u8BA1\u7B97\u7ED3\u679C\n- error: \u9519\u8BEF\u4FE1\u606F\uFF08\u5982\u679C\u5931\u8D25\uFF09\n\n\u4F7F\u7528\u793A\u4F8B:\n- calculator({ operation: 'add', a: 5, b: 3 }) // \u8FD4\u56DE 8\n- calculator({ operation: 'multiply', a: 4, b: 7 }) // \u8FD4\u56DE 28\n- calculator({ operation: 'divide', a: 10, b: 2 }) // \u8FD4\u56DE 5\n\n\u6CE8\u610F\u4E8B\u9879:\n- \u9664\u6CD5\u8FD0\u7B97\u65F6\uFF0C\u9664\u6570\uFF08b\uFF09\u4E0D\u80FD\u4E3A 0\n- \u6240\u6709\u53C2\u6570\u90FD\u662F\u5FC5\u9700\u7684\n- \u8BA1\u7B97\u7ED3\u679C\u4E3A\u6D6E\u70B9\u6570\u7C7B\u578B";
